function set_profile(profile)
	if not table.containskey(monster_profiles, profile) then
		windower.add_to_chat(5, "Profile " .. profile .. " doesn't exist.  Check spelling and capitalization")
		return
	end
	
	auto_spells = monster_profiles[profile].auto_spells
	auto_abilities = monster_profiles[profile].auto_abilities
end